            Console.WriteLine(p.LongestPalindrome(""));
            Console.WriteLine(p.LongestPalindrome("abb"));
            Console.WriteLine(p.LongestPalindrome("babad"));
            Console.WriteLine(p.LongestPalindrome("cbbd"));
        }
    }
