using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;

namespace Rextester
{
    public class Program
    {
public int UniqueLetterString(string S) { 
    int len = S.Length; 
    var mapping = new Dictionary<char,int>(); 
    var mapping1 = new Dictionary<char,int>(); 
    long p = 0;
    long result = 0;
    int M = 1000000007;
    for(int i = 0;i<len;i++){ 
        if(!mapping.ContainsKey(S[i])){
            p = (p+i+1)%M; 
            mapping.Add(S[i],i); 
        }
        else{ 
            if(mapping1.ContainsKey(S[i])){ 
                p = Math.Abs(p+i-mapping[S[i]] - (mapping[S[i]]-mapping1[S[i]])); 
                mapping1[S[i]] = mapping[S[i]]; 
            }
            else{ 
                p = Math.Abs(p+i-mapping[S[i]] - (mapping[S[i]]+1)); 
                mapping1.Add(S[i],mapping[S[i]]); 
            } 
            mapping[S[i]] = i; 
        } 
        //Console.WriteLine(p); 
        result+=p; 
    } 
    return (int)(result%M); 
}
        
        public static void Main(string[] args)
        {
            //Your code goes here
            Console.WriteLine("Hello, world!");
            var p = new Program();
            Console.WriteLine(p.UniqueLetterString("BABABBABAA"));//35
            //6785470
            Console.WriteLine(p.UniqueLetterString(""));
            //6848884
            Console.WriteLine(p.UniqueLetterString(""));
            //Console.WriteLine(p.UniqueLetterString("AAA"));//3
            //Console.WriteLine(p.UniqueLetterString("ABA"));//8
            //Console.WriteLine(p.UniqueLetterString("ABC"));//10
        }
    }
}
