using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;

namespace Rextester
{
    public class Program
    {
    public int UniqueLetterString(string S) {
        int[,] dp = new int[S.Length, 26];
        int[] counts = new int[S.Length];
        
        for(int i=0;i<S.Length;i++){
            dp[i,S[i]-'A'] = 1;
            counts[i]=1;
        }
        long total = S.Length;
        for(int len=2;len<=S.Length;len++){
            for(int j=0;j<=S.Length-len;j++){
                int index = j+len-1;
                int c = S[j]-'A';
                
                //Console.WriteLine(total+" "+len+" "+index+" "+S[j]+" "+dp[index,c]+" "+string.Join(",",counts));

                if (dp[index, c]==0){ 
                    dp[index, c]++;
                    counts[index]++;
                }
                else if (dp[index, c]==1){
                    dp[index,c]++;
                    counts[index]--;
                }
                
                total += counts[index]; 
            }
        }
        return (int)(total%1000000007);
    }
        
        public static void Main(string[] args)
        {
            //Your code goes here
            Console.WriteLine("Hello, world!");
            var p = new Program();
            //6785470
            Console.WriteLine(p.UniqueLetterString(""));
            //6848884
            Console.WriteLine(p.UniqueLetterString(""));
            Console.WriteLine(p.UniqueLetterString("AAA"));//3
            Console.WriteLine(p.UniqueLetterString("ABA"));//8
            Console.WriteLine(p.UniqueLetterString("ABC"));//10
        }
    }
}
